def button_example():
    radio_keys = ['Play', 'Stop', 'Pause', 'Off']
    selected_color = ('red', 'white')
    active_radio_button = None

    layout = [[sg.Text('My Window')],
              [sg.Text('These are simulated radio buttons')],
              [sg.Button(name) for name in radio_keys],
              [sg.Button('Go'), sg.Button('Exit')]]

    window = sg.Window('Window Title', layout, use_default_focus=False)

    while True:  # Event Loop
        event, values = window.read()
        print(f'event: {event}')
        print(f'values: {event}')
        if event in (None, 'Exit'):
            break
        if event in radio_keys:
            for k in radio_keys:  # ALWAYS RESET THE COLORS
                window[k].update(button_color=sg.theme_button_color())
            window[event].update(button_color=selected_color)  # SET COLORS TO EVENT
            active_radio_button = event

    window.close()


def led_example():
    circle = '⚫'
    circle_outline = '⚪'

    def _led(color, key):
        return sg.Text(circle_outline, text_color=color, key=key)

    layout = [[sg.Text('Status 1  '), _led('Green', '-LED0-')],
              [sg.Text('Status 2  '), _led('blue', '-LED1-')],
              [sg.Text('Status 3  '), _led('red', '-LED2-')],
              [sg.Button('Exit')]]

    window = sg.Window('Window Title', layout, font='Any 16')

    while True:
        event, values = window.read(timeout=200)
        if event == sg.WIN_CLOSED or event == 'Exit':
            break
        # Loop through all of the LEDs and update. 25% of the time turn it off.
        for i in range(3):
            window[f'-LED{i}-'].update(circle)

    window.close()


def slide_example():
    layout = [[sg.Text('A toggle button example')],
              [sg.Text('A graphical version'),
               sg.Button('', image_data=TOGGLE_BTN_OFF, key='-TOGGLE-GRAPHIC-',
                         button_color=(sg.theme_background_color(), sg.theme_background_color()), border_width=0)],
              [sg.Button('On', size=(3, 1), button_color='white on green', key='-B-'), sg.Button('Exit')]]

    window = sg.Window('Toggle Button Example', layout)

    down = graphic_off = True
    while True:  # Event Loop
        event, values = window.read()
        print(event, values)
        if event in (sg.WIN_CLOSED, 'Exit'):
            break
        elif event == '-B-':  # if the normal button that changes color and text
            down = not down
            window['-B-'].update(text='On' if down else 'Off',
                                 button_color='white on green' if down else 'white on red')
        elif event == '-TOGGLE-GRAPHIC-':  # if the graphical button that changes images
            graphic_off = not graphic_off
            window['-TOGGLE-GRAPHIC-'].update(image_data=TOGGLE_BTN_OFF if graphic_off else TOGGLE_BTN_ON)


# ALL EXample
def make_window(theme=None):
    NAME_SIZE = 23

    def name(name):
        dots = NAME_SIZE - len(name) - 2
        return sg.Text(name + ' ' + '•' * dots, size=(NAME_SIZE, 1), justification='r', pad=(0, 0), font='Courier 10')

    sg.theme(theme)

    Menu = sg.Menu

    treedata = sg.TreeData()

    layout_header = [sg.Image('../static/bigfin_logo.png'), sg.T('Dcs5 Controller App', font='_ 20', justification='c')]
    layout_l = [
        [name('Combo'),
         sg.Combo(sg.theme_list(), default_value=sg.theme(), s=(15, 22), enable_events=True, readonly=True,
                  k='-COMBO-')],
        [name('OptionMenu'), sg.OptionMenu(['OptionMenu', 'Other Option', 'Another One'], s=(15, 2))],
        [name('Checkbox'), sg.Checkbox('Checkbox')],
        [name('Radio'), sg.Radio('Radio', 1)],
        [name('Button'), sg.Button('Button')],
        [name('Slider'), sg.Slider((0, 10), orientation='h', s=(10, 15))],
        [name('Graph'), sg.Graph((125, 50), (0, 0), (125, 50), k='-GRAPH-')]]

    layout_r = [
        [name('Horizontal Separator'), sg.HSep()],
        [name('Column'), sg.Column([[sg.T(s=15)]])],
                ]

    # Note - LOCAL Menu element is used (see about for how that's defined)

    layout = [[Menu([['File', ['Exit']],], k='-CUST MENUBAR-', p=0)],
              layout_header,
              [sg.Col(layout_l, p=0), sg.Col(layout_r, p=0)]]

    window = sg.Window('The PySimpleGUI Element List', layout, finalize=True,
                       right_click_menu=sg.MENU_RIGHT_CLICK_EDITME_VER_EXIT, keep_on_top=True)

    return window


def run_example():
    window = make_window()

    while True:
        event, values = window.read()
        # sg.Print(event, values)
        if event == sg.WIN_CLOSED or event == 'Exit':
            break

        if values['-COMBO-'] != sg.theme():
            sg.theme(values['-COMBO-'])
            window.close()
            window = make_window()
    window.close()


